import react from 'react';
import {handlesearchtext} from './Actions/index';
class Navbar extends react.Component
{
    constructor()
    {
        super();
        this.state = {
            searchtext : '',
            showsearchtext : false
        }
    }
    changehandler = (e)=>
    {
        this.setState({
            searchtext : e.target.value
        });
    }
    searchhandler()
    {
        const {searchtext} = this.state; 
        this.props.store.dispatch(handlesearchtext(searchtext));
    }
    render()
    {return(
        <div className = 'nav'>
        <div className = 'nav-container'>
        <input type='text' className = 'searchbar' onChange = {this.changehandler}></input>
        <div className = 'search-button' on>Search</div>
        </div>
        </div>
    );}
}
export default Navbar;
