const initialmoviet = {
    movies:[],
    favorites:[],
    showfavorites:false
}
const initialrootstate = {
    movies:{},
    search:{}
}
function Addmovies(state = emptyobject,action){
    if(action.type === 'ADD_MOVIES')return {
        ...state,movies:action.movies
    }
    else if(action.type === 'ADD_FAVORITE')return {
        ...state,favorites:[action.favoritemovie,...state.favorites]
    }
    else if(action.type === 'SHOW_FAVORITES')return {
        ...state,showfavorites:action.val
    }
    else if(action.type === 'SHOW_MOVIES')return {
        ...state,showfavorites:action.val
    }
    else return state;
}
export default Addmovies;
