import react from 'react';
class Moviecard extends react.Component
{
    handlefavoritebutton =(movie)=>
    {
        this.props.store.dispatch({
            type:'ADD_FAVORITES',
            favorites:[movie]
        });
        console.log(this.props.store.getState());
    }
    render()
    {return(
        <div className = 'moviecard-container'>
        <img src = {this.props.movie.Poster} alt='movie-'/>
        <div className = 'movie-data'>
        <div className = 'movie-title'>{this.props.movie.Title}</div>
        <div className = 'movie-description'>{this.props.movie.Plot}</div>
        <div className = 'movie-rating'>{this.props.movie.Ratings[0].Value}</div></div>
        <button className = 'favorites'> Add Favorite</button>
        </div>
    );}
}
export default Moviecard;
